!function(){var t,e=document.getElementById("darkSwitch");if(e){t=null!==localStorage.getItem("darkSwitch")&&"dark"===localStorage.getItem("darkSwitch"),(e.checked=t)?document.body.setAttribute("data-theme","dark"):document.body.removeAttribute("data-theme"),e.addEventListener("change",function(t){e.checked?(document.body.setAttribute("data-theme","dark"),localStorage.setItem("darkSwitch","dark")):(document.body.removeAttribute("data-theme"),localStorage.removeItem("darkSwitch"))})}}();

function setDarkMode() {
    Array.prototype.map.call(document.querySelectorAll(".navbar-light"), function (element) {
        element.classList.remove("navbar-light");
        element.classList.add("navbar-dark");
    });
    Array.prototype.map.call(document.querySelectorAll(".bg-light"), function (element) {
        element.classList.remove("bg-light");
        element.classList.add("bg-dark");
    });
}

function setLightMode() {
    Array.prototype.map.call(document.querySelectorAll(".navbar-dark"), function (element) {
        element.classList.remove("navbar-dark");
        element.classList.add("navbar-light");
    });
    Array.prototype.map.call(document.querySelectorAll(".bg-dark"), function (element) {
        element.classList.remove("bg-dark");
        element.classList.add("bg-light");
    });
}

function changeBootstrapColors() {
    if (localStorage.darkSwitch === "dark" || document.querySelector("#darkSwitch").checked) {
        if (document.querySelectorAll(".navbar-light").length > 0) {
            setDarkMode();
        } else {
            setLightMode();
        }
    } else {
        setLightMode();
    }
}

document.querySelector("#darkSwitch").addEventListener("click", function () {
    changeBootstrapColors();
});
changeBootstrapColors();
